import { defineStore } from 'pinia'
import {ref} from 'vue'
import axios from 'axios'

export  const useAppstore = defineStore('app', ()=>{
        //variables
        let  modal = ref(false)
        let fotos = ref([])

        
        //funciones
        const showModal = () =>{ 
                modal.value= !modal.value
            
            return modal
        }

        const getDogs = () => {
            for(var i=0; i<6; i++){
                axios.get('https://dog.ceo/api/breeds/image/random')
                    .then(res => {
                        data = res.json()
                        fotos.value.push({
                            'id': i,
                            'foto': data.message
                        })
                    })
                    .catch( e => {
                            console.log(e)
                        }
                    )

            }

            return fotos
        }

        //return
        return{
            modal,
            showModal,
            getDogs,
        }
    }
)
