import { defineStore } from 'pinia'
import {ref} from 'vue'


export  const useAppstore = defineStore('app', ()=>{
        //variables
        let  modal = true
        
        //funciones
        const showModal = () =>{
            modal = !modal
            return modal
        }

        //return
        return{
            modal,
            showModal
        }
    }
)
