import { defineStore } from 'pinia'
import {ref} from 'vue'


export  const useAppstore = defineStore('app', ()=>{
        //variables
        let  modal = ref(false)
        
        //funciones
        const showModal = () =>{ 
                modal.value= !modal.value
            
            return modal
        }

        //return
        return{
            modal,
            showModal
        }
    }
)
