import { defineStore } from 'pinia'
import {ref} from 'vue'


export  const useAppstore = defineStore('app', ()=>{
        //variables
        let  modal = ref(false)
        
        //funciones
        const showModal = () =>{
            if(modal.value ){
                modal.value+=false
            }else{
                modal.value+=true    
            } 
            return modal.value
        }

        //return
        return{
            modal,
            showModal
        }
    }
)
